mapper.update:

UPDATE good_check
SET good_check = good_check + 1 
WHERE m_index=#{m_index} AND board_index=#{board_index}


UPDATE good_check
SET good_check = 0
WHERE m_index=#{m_index} AND board_index=#{board_index}

mapper.select:

SELECT good_check
FROM good_check
WHERE m_index = #{m_index} AND board_index = #{board_index}
