mapper.select:

select * from course a join map b 
	on b.lc_index = a.lc_index
	where b.lc_index = #{value}


SELECT COUNT(*) FROM member 
WHERE 
M_ID = #{m_id}
AND
M_PW_1 = #{m_pw}

mapper.insert:


		select mp_index from mycourse
		where lc_index = #{lc_index}
		and m_index = #{m_index}



insert into myrecord values(
	'pr_'||lpad(seq_pr.nextval, 10, '0'), #{mp_index}, #{m_index}, #{lc_title}, #{pr_text}, 0, 0, #{pr_resistdate}, sysdate, #{pr_record}, null, #{pr_type}, #{pr_public}
)

mapper.update:

update myrecord set
	pr_thumbnail = #{pr_thumbnail}
WHERE pr_index = #{pr_index}
